using Knowledge.API.Models;
using Knowledge.Services;
using Knowledge.Services.Configuration;
using Knowledge.Services.Metadata;
using Knowledge.Services.Models;
using Microsoft.AspNetCore.Mvc;
using Newtonsoft.Json.Linq;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;

namespace Knowledge.API.Controllers
{
    [Route("api/[controller]")]
    [ApiController]
    //[Authorize]
    public class SearchController : AbstractApiController
    {
        private IMetadataService metadataService { get; set; }

        private const string DEFAULT_INDEX_NAME = "index";

        private static readonly string WHITE_IMAGE_BASE64 = "";

        public SearchController(IQueryService client, SearchServiceConfig svcconfig, IMetadataService msvc)
        {
            _queryService = client;
            _config = svcconfig;
            this.metadataService = msvc;
        }

        [HttpPost("getdocuments")]
        public async Task<IActionResult> GetDocumentsAsync(ApiSearchRequest request)
        {
            request.indexName = DEFAULT_INDEX_NAME;
            request.permissions = GetUserPermissions();
            SearchResponse result = await _queryService.GetDocumentsAsync(request);

            return CreateContentResultResponse(result);
            //return new JsonResult(result);
        }

        [HttpPost("getdocumentbyindexkey")]
        public async Task<IActionResult> GetDocumentByIndexKeyAsync(ApiSearchRequest request)
        {
            if (!string.IsNullOrEmpty(request.index_key))
            {
                request.indexName = DEFAULT_INDEX_NAME;
                request.permissions = GetUserPermissions();
                var result = await _queryService.GetDocumentByIndexKey(request);

                return CreateContentResultResponse(result);
            }

            return new BadRequestResult();
        }

        [HttpPost("getdocumentbyid")]
        public async Task<IActionResult> GetDocumentByIdAsync(ApiSearchRequest request)
        {
            if (!string.IsNullOrEmpty(request.document_id))
            {
                request.indexName = DEFAULT_INDEX_NAME;
                request.permissions = GetUserPermissions();
                var result = await _queryService.GetDocumentById(request);

                return CreateContentResultResponse(result);
            }

            return new BadRequestResult();
        }

        [HttpGet("getdocumentcoverimage")]
        public async Task<IActionResult> GetDocumentCoverImageAsync(string id)
        {
            if (!string.IsNullOrEmpty(id))
            {
                ApiSearchRequest request = new ApiSearchRequest
                {
                    document_id = id
                };
                request.indexName = DEFAULT_INDEX_NAME;
                request.permissions = GetUserPermissions();

                var result = await _queryService.GetDocumentCoverImage(request);

                if (result.Count > 0)
                {
                    JObject document = (JObject)result.Results[0]["Document"];

                    JObject image = (JObject) document.GetValue("image");

                    if (String.IsNullOrEmpty((string)image.GetValue("thumbnail_medium")))
                    {
                        //TODO return a no preview watermark image (base64)
                        return Content(WHITE_IMAGE_BASE64);
                    }
                    else
                    {
                        return new FileContentResult(System.Convert.FromBase64String((string)image.GetValue("thumbnail_medium")), "image/jpeg");
                    }
                }
                else
                {
                    //TODO return a no preview watermark image (base64)
                    return Content(WHITE_IMAGE_BASE64);
                }
            }

            return new BadRequestResult();
        }

        [HttpPost("getlatestdocuments")]
        public async System.Threading.Tasks.Task<IActionResult> GetLatestDocumentsAsync(ApiSearchRequest request)
        {
            request.indexName = DEFAULT_INDEX_NAME;
            request.permissions = GetUserPermissions();
            SearchResponse result = await _queryService.GetLatestDocumentsAsync(request);

            return CreateContentResultResponse(result);
        }

        [HttpPost("getdocumentmetadata")]
        public async Task<IActionResult> GetDocumentMetadataAsync(ApiMetadataRequest request)
        {
            if (!string.IsNullOrEmpty(request.path))
            {
                var result = await metadataService.GetDocumentMetadataAsync(request.path);

                return CreateContentResultResponse(result);
            }

            return new BadRequestResult();
        }

        [Route("autocomplete")]
        [HttpGet]
        public async Task<IActionResult> AutoCompleteAsync(string term, string targetField = "key_phrases", string suggester = "suggester1", string indexName = DEFAULT_INDEX_NAME)
        {
            List<string> candidates = new List<string>();

            if (!string.IsNullOrEmpty(term))
            {
                ApiSuggestionRequest request = new ApiSuggestionRequest
                {
                    indexName = indexName,
                    term = term,
                    targetField=targetField,
                    suggester=suggester
                };

                // Change to _docSearch.Suggest if you would prefer to have suggestions instead of auto-completion
                var response = await _queryService.AutocompleteAsync(request);

                if (response != null)
                {
                    candidates = response;
                }
            }

            return CreateContentResultResponse(candidates);
        }

        [Route("suggest")]
        [HttpGet]
        public async Task<IActionResult> SuggestAsync(string term, string targetField = "key_phrases", bool fuzzy = true, bool highlights = true, string suggester = "suggester1", string indexName = DEFAULT_INDEX_NAME, string filter = null)
        {
            List<string> uniqueItems = new List<string>();

            if (!string.IsNullOrEmpty(term))
            {
                ApiSuggestionRequest request = new ApiSuggestionRequest
                {
                    indexName = indexName,
                    term = term,
                    targetField = targetField,
                    fuzzy=fuzzy,
                    highlights=highlights,
                    suggester = suggester,
                    filter = filter
                };

                // Change to _docSearch.Suggest if you would prefer to have suggestions instead of auto-completion
                var response = await _queryService.SuggestAsync(request);

                if (response != null)
                {
                    uniqueItems = response.Distinct().ToList();
                }
            }

            return CreateContentResultResponse(uniqueItems);
        }

